import React, { useState } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  FlatList,
  Image,
} from 'react-native';
import QRScanner from 'react-native-qrcode-scanner';

const UserPage = () => {
  const [scanResult, setScanResult] = useState('');
  const [showScanner, setShowScanner] = useState(false);

  const handleScan = (data) => {
    if (data) {
      setScanResult(data);
      setShowScanner(false);
    }
  };

  const handleError = (err) => {
    console.error(err);
  };

  const openScanner = () => {
    setShowScanner(true);
  };

  const closeScanner = () => {
    setShowScanner(false);
  };

  const gallery = [
    // Add your gallery data here, for example:
    // { id: 1, title: 'Sneaker 1', img: '/path/to/sneaker1.jpg' },
    // { id: 2, title: 'Sneaker 2', img: '/path/to/sneaker2.jpg' },
  ];

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Sneaker Authenticator</Text>
      <Text style={styles.subtitle}>User Dashboard</Text>
      {showScanner ? (
        <View>
          <QRScanner
            onRead={handleScan}
            cameraStyle={styles.camera}
            reactivate={true}
            reactivateTimeout={5000}
          />
          <TouchableOpacity style={styles.button} onPress={closeScanner}>
            <Text style={styles.buttonText}>Close Scanner</Text>
          </TouchableOpacity>
        </View>
      ) : (
        <TouchableOpacity style={styles.button} onPress={openScanner}>
          <Text style={styles.buttonText}>Scan QR Code</Text>
        </TouchableOpacity>
      )}
      <Text style={styles.galleryTitle}>My Sneaker Gallery</Text>
      <FlatList
        data={gallery}
        renderItem={({ item }) => (
          <View style={styles.card}>
            <Image source={{ uri: item.img }} style={styles.image} />
            <Text style={styles.cardTitle}>{item.title}</Text>
          </View>
        )}
        keyExtractor={(item) => item.id.toString()}
        numColumns={3}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center',
    marginBottom: 10,
    },
    subtitle: {
    fontSize: 18,
    fontWeight: '600',
    textAlign: 'center',
    marginBottom: 20,
    },
    button: {
    backgroundColor: '#1E90FF',
    padding: 10,
    borderRadius: 5,
    marginBottom: 20,
    },
    buttonText: {
    color: '#FFF',
    fontSize: 18,
    textAlign: 'center',
    },
    camera: {
    height: 300,
    width: '100%',
    marginBottom: 20,
    },
    galleryTitle: {
    fontSize: 20,
    fontWeight: '600',
    marginBottom: 10,
    },
    card: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    margin: 5,
    padding: 5,
    backgroundColor: '#F0F0F0',
    borderRadius: 5,
    },
    image: {
    width: '100%',
    height: 100,
    resizeMode: 'cover',
    borderRadius: 5,
    },
    cardTitle: {
    marginTop: 5,
    fontSize: 16,
    fontWeight: '500',
    },
    });
    
    export default UserPage;
