import React, { useState } from 'react';
import {
  Container,
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Button,
} from '@mui/material';
import { purple } from '@mui/material/colors';
import QRScanner from 'react-qr-scanner';

const UserPage = () => {
  const [scanResult, setScanResult] = useState('');
  const [showScanner, setShowScanner] = useState(false);

  const handleScan = (data) => {
    if (data) {
      setScanResult(data);
      setShowScanner(false);
    }
  };

  const handleError = (err) => {
    console.error(err);
  };

  const openScanner = () => {
    setShowScanner(true);
  };

  const closeScanner = () => {
    setShowScanner(false);
  };

  const gallery = [
    // Add your gallery data here, for example:
    // { id: 1, title: 'Sneaker 1', img: '/path/to/sneaker1.jpg' },
    // { id: 2, title: 'Sneaker 2', img: '/path/to/sneaker2.jpg' },
  ];

  return (
    <Container maxWidth="md">
      <Box sx={{ my: 4 }}>
        <Typography variant="h4" sx={{ fontWeight: 'bold', color: purple[900], mb: 2 }}>
          Sneaker Authenticator
        </Typography>
        <Typography variant="h6" sx={{ color: purple[800], mb: 3 }}>
          User Dashboard
        </Typography>
        {showScanner ? (
          <Box>
            <QRScanner
              onScan={handleScan}
              onError={handleError}
              style={{ width: '100%' }}
            />
            <Button
              variant="contained"
              color="secondary"
              sx={{ backgroundColor: purple[900], fontWeight: 'bold', mt: 2 }}
              fullWidth
              onClick={closeScanner}
            >
              Close Scanner
            </Button>
          </Box>
        ) : (
          <Button
            variant="contained"
            color="secondary"
            sx={{ backgroundColor: purple[700], fontWeight: 'bold', mb: 3 }}
            fullWidth
            onClick={openScanner}
          >
            Scan QR Code
          </Button>
        )}
        <Typography variant="h6" sx={{ color: purple[800], mb: 2 }}>
          My Sneaker Gallery
        </Typography>
        <Grid container spacing={2}>
          {gallery.map((item) => (
            <Grid item key={item.id} xs={12} sm={6} md={4}>
              <Card>
                <CardMedia
                  component="img"
                  height="200"
                  image={item.img}
                  alt={item.title}
                />
                <CardContent>
                  <Typography gutterBottom variant="h6" component="div">
                    {item.title}
                  </Typography>
                </CardContent>
              </Card>
             </Grid>
          ))}
        </Grid>
      </Box>
    </Container>
  );
};

export default UserPage;
